xwikidoc.content:
{{groovy}}
def escapetool = new org.xwiki.velocity.tools.EscapeTool()
def space = "";
def nb = 10
def maxlength = 30
if (request.space)
 space = request.space
if (request.nb)
 nb = Integer.parseInt(request.nb)

println """
{{html clean=false}}
<form action="">
Space name: <input type="text" name="space" value="${escapetool.xml(space)}" size="60" /><br />
Nb: <input type="text" name="nb" value="${escapetool.xml(nb)}" size="10" /><br />
<input name="submit" type="submit" value="Go" class="button" />
</form>
{{/html}}
"""

if (request.space) {
  def duplicates = 0
  def nbconversions = 0
  def newnamelist = []
  def list = xwiki.search("select doc.name, doc.fullName from XWikiDocument as doc where doc.web='${space}' and length(doc.name)>${maxlength} order by doc.name", nb, 0)
  println "Number of too long names: ${list.size()}"
  for (item in list) {
    def name = item[0]
    def fullName = item[1]
    println "* ${fullName}"
    def sname = name.replaceAll("/","_").replaceAll("[.]","_").replaceAll(" _ ", "_").replaceAll("[^\\dA-Za-z_ ]"," ").replaceAll('_', " ")replaceAll("\\s+"," ")
    splitname = sname.split(' ')
    println "** Cleaned name: ${sname}"
    def newname = splitname[0]
    def scounter = 1
    while (newname.replaceAll("[0-9_]","")=="" || newname.size()<20) {
      if (scounter>5)
        break;
      if (scounter>=splitname.size())
        break;
      if ((newname.size() + splitname[scounter].size()) > maxlength)
        break;
      newname += " " + splitname[scounter]
      scounter++;
    }

    if (newname.endsWith("_")||newname.endsWith("_"))
     newname = newname.substring(0, newname.length()-1)

    def count = 1
    def newname2 = newname;
    while (newnamelist.contains(newname2) || xwiki.exists(space + "." + newname2)) {
      newname2 = newname + "_" + count;
      count++;
      println "** Duplicate found"
      duplicates++;
    }
    if (newname2.size()>=name.size()) {
     println "** New name proposal: ${newname2}"
     println "** New name proposal is too long. No conversion."
    } else {
     nbconversions++;
     newnamelist.add(newname2)
     println "** New name proposal: ${newname2}"
     println "** Name proposal length: ${newname2.size()}"
    }
  }

println ""
println ""
println "Number of too long names: ${list.size()}"
println "Number of conversions: ${nbconversions}"
println "Found duplicated: ${duplicates}"
println """[[Confirm page name length reduction>>||queryString="confirm=1"]]"""
}

{{/groovy}}
